import React from 'react';
import ChatBot from 'react-simple-chatbot';
import Review from './Review'

class App extends React.Component {
  render() {
    return (
      <ChatBot
        headerTitle="Remune"
        floating="true"
        steps={[
          {
            id: '1',
            message: 'What is your remune ID?',
            trigger: 'identity',
          },
          {
            id: 'identity',
            user: true,
            trigger: '3',
          },
          {
            id: '3',
            message: 'Hi {previousValue}! Please choose your plan',
            trigger: 'plan',
          },
          {
            id: 'plan',
            options: [
              { value: 'onetime', label: 'One Time', trigger: '5' },
              { value: 'daily', label: 'Daily', trigger: '5' },
              { value: 'weekly', label: 'Weekly', trigger: '5' },
              { value: 'monthly', label: 'Monthly', trigger: '5' },
            ],
          },
          {
            id: '5',
            message: 'How much xls you need to send?',
            trigger: 'amt',
          },
          {
            id: 'amt',
            user: true,
            trigger: '7',
            validator: (value) => {
              if (isNaN(value)) {
                return 'value must be a number';
              } else if (value < 0) {
                return 'value must be positive';
              } else if (value > 120) {
                return `${value}? Come on!`;
              }

              return true;
            },
          },
          {
            id: '7',
            message: 'Great! Check out your summary',
            trigger: 'review',
          },
          {
            id: 'review',
            component: <Review />,
            asMessage: true,
            trigger: 'update',
          },
          {
            id: 'update',
            message: 'Would you like to update some field?',
            trigger: 'update-question',
          },
          {
            id: 'update-question',
            options: [
              { value: 'yes', label: 'Yes', trigger: 'update-yes' },
              { value: 'no', label: 'No', trigger: 'end-message' },
            ],
          },
          {
            id: 'update-yes',
            message: 'What field would you like to update?',
            trigger: 'update-fields',
          },
          {
            id: 'update-fields',
            options: [
              { value: 'identity', label: 'Name', trigger: 'update-identity' },
              { value: 'plan', label: 'Gender', trigger: 'update-plan' },
              { value: 'amt', label: 'Age', trigger: 'update-amt' },
            ],
          },
          {
            id: 'update-identity',
            update: 'identity',
            trigger: '7',
          },
          {
            id: 'update-plan',
            update: 'plan',
            trigger: '7',
          },
          {
            id: 'update-amt',
            update: 'amt',
            trigger: '7',
          },
          {
            id: 'end-message',
            message: 'Successfully completed the transaction!',
            end: true,
          },
        ]}
      />
    );
  }
}

export default App;
