import React from 'react';
import ChatBot from 'react-simple-chatbot';
import Review from './Review'

class App extends React.Component {
  render() {
    return (
      <ChatBot
        headerTitle="Remune"
        floating="true"
        steps={[
  {
    id: '1',
    message: 'What is your remune ID?',
    trigger: 'remune_id'
  },
  {
    id: 'remune_id',
    user: true,
    trigger: '3'
  },
  {
    id: '3',
    message: 'Hi {previousValue}! Please choose your plan',
    trigger: 'plan'
  },
  {
    id: 'plan',
    options: [
      {
        value: 'onetime',
        label: 'One Time',
        trigger: '5'
      },
      {
        value: 'daily',
        label: 'Daily',
        trigger: '5'
      },
      {
        value: 'weekly',
        label: 'Weekly',
        trigger: '5'
      },
      {
        value: 'monthly',
        label: 'Monthly',
        trigger: '5'
      }
    ]
  },
  {
    id: '5',
    message: 'How much xls you need to send?',
    trigger: 'amount',

  },
  {
    id: 'amount',
    user: true,
    trigger: '7',
validator: (value) => {
              if (isNaN(value)) {
                return 'value must be a number';
              } else if (value < 0) {
                return 'value must be positive';
              } else if (value > 120) {
                return `${value}? Come on!`;
              }

              return true;
            },
  },
  {
    id: '7',
    message: 'Great! Check out your summary',
    trigger: 'review'
  },
  {
    id: 'review',
    component: <Review/>,
    asMessage: true,
    trigger: 'update'
  },
  {
    id: 'update',
    message: 'Would you like to update some field?',
    trigger: 'update-question'
  },
  {
    id: 'update-question',
    options: [
      {
        value: 'yes',
        label: 'Yes',
        trigger: 'update-yes'
      },
      {
        value: 'no',
        label: 'No',
        trigger: 'end-message'
      }
    ]
  },
  {
    id: 'update-yes',
    message: 'What field would you like to update?',
    trigger: 'update-fields'
  },
  {
    id: 'update-fields',
    options: [
      {
        value: 'remune_id',
        label: 'Remune ID',
        trigger: 'update-retune_id'
      },
      {
        value: 'plan',
        label: 'Plan',
        trigger: 'update-plan'
      },
      {
        value: 'amount',
        label: 'XLS',
        trigger: 'update-amount'
      }
    ]
  },
  {
    id: 'update-retune_id',
    update: 'retune_id',
    trigger: '7'
  },
  {
    id: 'update-plan',
    update: 'plan',
    trigger: '7'
  },
  {
    id: 'update-amount',
    update: 'amount',
    trigger: '7'
  },
  {
    id: 'end-message',
    message: 'Successfully completed the transaction!',
    end: true
  }
]}
      />
    );
  }
}

export default App;
